 import React from 'react'
 import "./style.css";
 import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
 import { faCoffee } from '@fortawesome/free-solid-svg-icons'
 import { FcApproval } from "react-icons/fc";
 import { FaPlus,FaMarker,FaPen,FaTrash } from "react-icons/fa";
import { useState } from 'react';
  const Todo =()=>{
      const[inputdata,setInputData]=useState("");
      const [items,setItems]=useState([]);
      //add the item func
      const addItem=()=>
      {
          if(!inputdata)
          {
              alert("pls fill the data");
          }
          else{
              const myNewInputData={
                  id:new Date().getTime().toString(),
                  name:inputdata  
              }; 
              setItems([...items,myNewInputData]);
              setInputData("");
          }
      }
      return(
          <>
        <div className="main-div">
            <div className='child_div'>
                <figure>
                    <img src='todo.svg' width='50px' alt="todologo" />
                    <figcaption>Add your List here </figcaption>
                </figure>
                <div className='addItems'>
                    <input type="text"
                    placeholder="ðŸ§ add items" className="form-control"
                        value={inputdata}
                        onChange={(event)=>setInputData(event.target.value)}
                    />
                  <FaPlus className='fa fa-plus add-btn' onClick={addItem}/>
                </div>
                {/*show our items*/}
               <div className='showItems'>
               {
                   items.map((curElem,index)=>{
                       return(
                        <div className='eachItem' key={index}>
                       <h3>{curElem.nme}</h3>
                       <div className='todo-btn'>
                       <FaPen className='fa fa-plus add-btn' />
                       <FaTrash className='fa fa-plus add-btn' />

                       </div>
                   </div>
                       );
                   })
               }
                  
               </div>
                <div className='showItems'>
                <button className='btn effect04' data-sm-link-text="Remove All">
                <span>
                    Check list
                    </span>

                </button>

                </div>
            </div>
        </div>
          </>
      );
  }
  export default Todo;
